mapper.update:
UPDATE doc
title = #{title}, summary = #{summary}, category_id = #{categoryId}, cost_score = #{costScore}, file_name = #{fileName}, file_path = #{filePath}, file_type = #{fileType}, file_size = #{fileSize}, file_md5 = #{fileMd5}, file_pages = #{filePages}, views = #{views}, uploader = #{uploader}, uploader_type = #{uploaderType}, create_time = #{createTime}, state = #{state},
WHERE id = #{id};

UPDATE doc
SET state = #{state}
WHERE id = #{docId}

UPDATE doc
SET state = #{toState}


        #{id}


        AND state = #{fromState}


    id = -1

UPDATE doc
SET category_id = #{categoryId}
WHERE id = #{docId}

mapper.select:
SELECT
, COUNT(dd.doc_id) as downloads
FROM doc d
LEFT JOIN doc_category c ON d.category_id=c.id
LEFT JOIN doc_download dd ON d.id = dd.doc_id
LEFT JOIN user u ON u.id = d.`uploader`

GROUP BY d.id
ORDER BY d.create_time ${sortType}
ORDER BY d.${sortBy} ${sortType}
ORDER BY downloads ${sortType}
LIMIT #{limit}
OFFSET #{offset}

SELECT COUNT(id)
FROM doc_download

    AND create_time >= #{beginTime}
    AND create_time <= #{endTime}

SELECT
LEFT JOIN doc_category c ON d.category_id=c.id LEFT JOIN doc_download dd ON dd.doc_id =d.id LEFT JOIN `user` u ON u.id =d.uploader AND d.uploader_type = 2
GROUP BY d.id ORDER BY weight DESC ,d.`state` DESC , downloads DESC,d.create_time DESC LIMIT #{limit} OFFSET #{offset}

SELECT *
FROM doc
WHERE file_md5 = #{md5}

SELECT *
FROM doc
WHERE file_type = #{type}

SELECT

FROM doc d

    #{type}

SELECT (
           SELECT IFNULL(SUM(score_change), 0)
           FROM user_score_history
           WHERE `type` = 1 AND object_id = #{docId}
       ) + (
           SELECT IFNULL(SUM(ush.score_change), 0)
           FROM doc_download dd
               JOIN user_score_history ush ON dd.id = ush.object_id AND ush.type = 3
           WHERE doc_id = #{docId}
       ) AS total
